query subscriptions(
  $subscriptionKeys: [String!]!
  $filters: [FilterInput!]!
  $breakdownFilters: [FilterInput!]!
) {
  subscriptions(
    subscriptionKeys: $subscriptionKeys
  ) {
    breakdown(
        type: MONTHLY
        filters: $breakdownFilters
    ) {
        fromDate
        toDate
        totals {
            currency
            moneyIn {
                average
                count
                sum
            }
            moneyOut {
                average
                count
                sum
            }
        }
    }
    availableBalances {
        subscriptionKey
        balance
        balanceWithCredit
        currency
        credit {
            available
            limit
            utilised
            utilisedUnauthorised
        }
    }
    transactions(
      filters: $filters
    ) {
      summary {
        moneyIn
        moneyOut
      }
      results {
        subscriptionKey
        transactionId
        correlationId
        transactionCode
        isCredit
        amount {
          value
          currency
        }
        dateTime
        bookingDateTime
        valueDateTime
        state
        category
        autoCategory
        availableBalance
        clearedBalance
      }
    }
  }
}

