  ) {
    breakdown(
        type: MONTHLY
        filters: $breakdownFilters
    ) {
        fromDate
        toDate
        totals {
            currency
            moneyIn {
                average
                count
                sum
            }
            moneyOut {
                average
                count
                sum
            }
